queries.query:
SELECT DISTINCT * WHERE {
    rubent:me foaf:knows ?person.
    ruben:me foaf:knows ?person.
    ?person foaf:name ?name.
}

SELECT ?article ?coAuthor
WHERE {
  ?myArticle a schema:ScholarlyArticle;
             foaf:maker ?author, <https://www.rubensworks.net/#me>.
  ?article a schema:ScholarlyArticle;
           foaf:maker ?coAuthor.
  FILTER(?coAuthor != <https://www.rubensworks.net/#me>)
}

PREFIX schema: <http://schema.org/>
PREFIX foaf: <http://xmlns.com/foaf/0.1/>
SELECT *
WHERE {
  ?article a schema:ScholarlyArticle;
  		   foaf:maker|schema:author ?author;
           foaf:name|schema:name ?name.
}

SELECT DISTINCT ?test ?type ?name ?comment WHERE {
  ?spec mf:conformanceRequirement (?subManifest).
  {
    ?test a mf:QueryEvaluationTest.
  } UNION {
    ?test a mf:UpdateEvaluationTest.
  }
  ?test a ?type.
  ?test mf:name ?name.
  ?test rdfs:comment ?comment.
}

PREFIX bookm: <http://www.w3.org/2002/01/bookmark#>
SELECT * WHERE {
  ?bookmark a bookm:Bookmark;
    bookm:hasTopic ?topic.
}

PREFIX foaf: <http://xmlns.com/foaf/0.1/>
PREFIX sioc: <http://rdfs.org/sioc/ns#>
SELECT ?message ?author WHERE {
  ?id sioc:content ?message.
  ?id foaf:maker ?author.
}

PREFIX ldp: <http://www.w3.org/ns/ldp#>
SELECT ?container ?resource WHERE {
  ?container ldp:contains ?resource.
  ?resource a ldp:Resource.
}

PREFIX schema: <https://schema.org/>
SELECT * WHERE {
  ?movie a schema:Movie.
}

PREFIX schema: <https://schema.org/>
SELECT * WHERE {
  ?movie a schema:Movie.
  ?action a schema:WatchAction;
          schema:object ?movie.
}

SELECT DISTINCT ?subject ?label WHERE {
  { ?subject dc:title ?label }
  UNION
  { ?subject rdfs:label ?label }
}
LIMIT 100

SELECT DISTINCT ?type ?label WHERE {
  ?s a ?type.
  OPTIONAL {
    ?type rdfs:label ?label.
  }
 FILTER LANGMATCHES(LANG(?label), "EN")
}
LIMIT 100
